import mysql from 'mysql2';

require("dotenv").config();

const pool = mysql.createPool({
    host: 'localhost',
    port: 3306,
    user: 'root',
    password: 'Woojinop22!',
    database: 'week5',
});

const promisePool = pool.promise();

export const selectSql = {
    getUser: async () => {
        const sql = `select * from user`;
        const [result] = await promisePool.query(sql);
        return result;
    },
    getstudent: async () => {
        const sql = `select * from student`;
        const [result] = await promisePool.query(sql);
        return result;
    },
    getstudentclass: async () => {
        const sql = `SELECT Student_id, Class_Id, Name, Professor, Number_of_participants FROM student_class`;
        const [result] = await promisePool.query(sql);
        return result;
    },
    getDepartment: async () => {
        const sql = `SELECT * FROM department2`; 
        const [result] = await promisePool.query(sql);
        return result;
    },
}

export const deleteSql = {
    deleteDepartment: async (data) => {
        console.log('delete department Dnumber =', data);
        const sql = `delete from department where Dnumber=${data.Dnumber}`
        console.log(sql);
        await promisePool.query(sql);
    },
    deletestudentclass: async (data) => {
        console.log('delete Class_Id =', data);
        const sql = `DELETE FROM student_class WHERE Class_Id=${data.Class_Id}`; 
        console.log(sql);
        await promisePool.query(sql);
    },
};

