                    throw new Error(`Update failed. No matching cart item found for Book ISBN: ${data.bookISBN}`);
                }
                return result;
            } catch (error) {
                console.error("Error in updatecart:", error);
                throw error;
            }
        },
        updatestock: async (data) => {
            const sql = `
